                if "otherIdentifier" in vckit_issuer: # Updates 'otherIdentifier' to 'issuerAlsoKnownAs' and retains the position
                    self._pop_and_replace_key(vckit_issuer, "otherIdentifier", "issuerAlsoKnownAs")

